<?phpclass Tx_AtolCmisList_Controller_CmisListController extends Tx_Extbase_MVC_Controller_ActionController {  /**	* @var String	**/  protected $folderId;    /**  * @var string  **/  protected $sID;    /**	* @var Tx_AtolCmisList_Cmis_Repository_ObjectRepositoryInterface	**/	protected $objectRepository;  	/**  * @var Tx_AtolCmisList_Cmis_Loader  **/  protected $loader;        /**	* @param Tx_AtolCmisList_Cmis_LoaderInterface $loader	* @return void	**/	public function injectLoader(Tx_AtolCmisList_Cmis_LoaderInterface $loader) {			$this->loader = $loader;	}    /**	* @param Tx_AtolCmisList_Cmis_Repository_ObjectRepositoryInterface objectRepository	* @return void	**/	public function injectObjectRepository(Tx_AtolCmisList_Cmis_Repository_ObjectRepositoryInterface $objectRepository){		$this->objectRepository=$objectRepository;	}  public function initializeAction(){    $repositoryFolderInfo = $this->getRepositoryFolderInfo();    $this->sID=$repositoryFolderInfo[2];    $this->loader->setUid($this->sID);    $this->folderId = $repositoryFolderInfo[1];  }    /**  * @param string $folderId   * @return void  **/  public function listAction($folderId=null){    if($this->folderId){      $objs = $this->objectRepository->listObjects($this->folderId);    }    /*$persistenceManager = Tx_Extbase_Dispatcher::getPersistenceManager();		$persistenceManager->persistAll();*/    $this->view->assign('objs',$objs);    $this->view->assign('type',$this->settings['display']['type']);    $this->view->assign('icons',$this->settings['display']['icons']);  }  /**  * @param string $oID  * @return void  **/  public function showAction($oID){      $obj=$this->objectRepository->findById($oID);    $this->view->assign('obj',$obj);    $this->view->assign('sID',$this->sID);  }       /**  * @return array Array with repository & folder info  **/  public function getRepositoryFolderInfo(){    $params = explode(';;_tx_;;',$this->settings['repositoryFolder']);    return $params;  }}?>